import React from 'react';

class Converter extends React.Component{

    constructor(props){
        super(props);

        this.state = new Object;
        this.state.currencyToConvert = "";
        this.state.currencyConversion = 0;
        this.state.currencyA = "USD";
        this.state.currencyB = "BRL";
        this.state.allCurrencies = {};
        
    }

    convert = () =>{

        const queryCurrencyData = `${this.state.currencyA}_${this.state.currencyB}`; 
        const url = `https://free.currconv.com/api/v7/convert?q=${queryCurrencyData}&compact=y&apiKey=a711cb5507804a084d63`;

        fetch(url).then(res=>{
            return res.json();
        }).then(json => {
            const conversion = json[queryCurrencyData]['val'];
            this.setState({
                currencyConversion : (parseFloat(this.state.currencyToConvert) * conversion).toFixed(2)
            });
        })

    }


    setCurrencyA = () =>{
        const url = `https://free.currconv.com/api/v7/currencies?apiKey=a711cb5507804a084d63`;

        let currencies = {}
      
        fetch(url).then(res=>{
          return res.json();
        }).then(json => {
          const currencies = json["results"];
          this.setState({allCurrencies : currencies});
        })
    }

    setCurrencyB = () =>{
        
    }


    render(){
        console.log(this.state.allCurrencies);
        
        return(
            <div className="converter">
                <select onClick={this.setCurrencyA}>
                    
                </select>
                <h2>{this.state.currencyA} to {this.state.currencyB}</h2>
                <input type="text" 
                onChange={(event) => this.setState({currencyToConvert : event.target.value})}/>
                <a onClick={this.convert}>Button</a>
                <h2>Converted Value: {this.state.currencyConversion}</h2>
            </div>
        );
    }
}

export default Converter;
