import React from 'react';

class Converter extends React.Component{

    constructor(props){
        super(props);

        this.state = new Object;
        this.state.currencyToConvert = "";
        this.state.currencyConversion = 0;
    }

    convert = () =>{
        this.setState({currencyToConvert : document.getElementById("val").value});

    }

    render(){
        return(
            <div className="converter">
                <h2>{this.props.currencyMain} to {this.props.currencyConverted}</h2>
                <input type="text" id="val" value=""/>
                <a onClick={this.convert}>Button</a>
                <h2>Converted Value</h2>
            </div>
        );
    }
}

export default Converter;
